import React, {Component} from 'react';
import {View, Text, PermissionsAndroid} from 'react-native';
import {OTSession, OTPublisher, OTSubscriber} from 'opentok-react-native';

class App extends Component {
  constructor(props) {
    super(props);
    this.apiKey = '472032';
    this.sessionId =
      '1_MX40NzIwMzJ-fjE3MTA4ODU0ODAzNzJ-Uy9jMmtzS3VMWTRPNmlSTmhoZFFZby9Jfn5-';
    this.token =
      '';
    this.state = {
      hasCameraPermission: true,
    };
    PermissionsAndroid.check(PermissionsAndroid.PERMISSIONS.CAMERA).then(permission => {
      this.setState({ hasCameraPermission: permission});
      console.log('PermissionsAndroid.PERMISSIONS.CAMERA', permission);
    })
  }

  render() {
    return (
      <View
        style={{
          flex: 1,
          flexDirection: 'column',
          paddingHorizontal: 100,
          paddingVertical: 50,
        }}>
        <Text>Test {String(this.state.hasCameraPermission)}</Text>
        <OTSession
          apiKey={this.apiKey}
          sessionId={this.sessionId}
          token={this.token}>
          <OTPublisher
            style={{width: 0, height: 0}}
            properties={{ videoSource: 'screen' }}
          />
          <OTSubscriber style={{width: 200, height: 200}} />
        </OTSession>
      </View>
    );
  }
}

export default App;
